import React, { useState, useEffect, useRef } from 'react';
import './ChatBot.css';
import axios from 'axios';

function ChatBot({ isOpen, onClose }) {
  const [messages, setMessages] = useState([
    { text: "Hello! I'm Dr. Aarogya AI. How can I help you today?", sender: 'bot' }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async () => {
    if (input.trim() === '') return;

    // Add user message to chat
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      // Send message to backend ML model
      const response = await axios.post('http://localhost:8000/chat', {
        message: input
      });

      // Add bot response to chat
      setTimeout(() => {
        setMessages(prev => [...prev, { 
          text: response.data.response || "I'm sorry, I couldn't process that request.", 
          sender: 'bot' 
        }]);
        setIsLoading(false);
      }, 600); // Small delay for natural feel

    } catch (error) {
      console.error('Error communicating with chatbot:', error);
      setMessages(prev => [...prev, { 
        text: "Sorry, I'm having trouble connecting right now. Please try again later.", 
        sender: 'bot' 
      }]);
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="chatbot-container">
      <div className="chatbot-header">
        <h3><i className="fas fa-robot"></i> Dr. Aarogya AI Assistant</h3>
        <button className="close-button" onClick={onClose}>Ã—</button>
      </div>

      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            {message.text}
          </div>
        ))}
        {isLoading && (
          <div className="message bot loading">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="chatbot-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your health question..."
        />
        <button onClick={handleSend} disabled={isLoading}>
          <i className="fas fa-paper-plane"></i>
        </button>
      </div>
    </div>
  );
}

export default ChatBot;
